mapper.insert:
INSERT INTO ROOM(
              ROOM_NO
            , ROOM_NAME
            , DEPTH
            , PARENT_NAME  
            ) VALUES(
              #{roomNo}
            , #{roomName}
            , #{depth}
            , #{parentName}
            )


  SELECT NVL(MAX(ROOM_NO), #{parentName}) + 1
  FROM ROOM
  WHERE PARENT_NAME = #{parentName}


INSERT INTO ROOM(
           ROOM_NO
          , ROOM_NAME
          , DEPTH
          , PARENT_NAME
          , INFO
          , PRICE
          , PEOPLE
          , ROOM_NUM  
          ) VALUES(
           #{roomNo}
          , #{roomName}
          , #{depth}
          , #{parentName}
          , #{info}
          , #{price}
          , #{people}
          , #{roomNum}
          )

SELECT CASE WHEN MAX(RD.ROOM_DETAIL_NO) IS NULL THEN #{roomNo} * 100 + 1 ELSE MAX(RD.ROOM_DETAIL_NO) + 1 END FROM ROOM R LEFT JOIN ROOM_DETAIL RD ON R.ROOM_NO = RD.ROOM_NO WHERE R.ROOM_NO = #{roomNo}
INSERT INTO ROOM_DETAIL( ROOM_NO , ROOM_NAME , ROOM_DETAIL_NO
) VALUES( #{roomNo} , #{roomName} ,#{roomDetailNo}
)

INSERT INTO ROOM_IMG(
              ROOM_NO
            , ROOM_IMG_NAME
            , UPLOAD_PATH
            , ROOM_IMG_NO
            , FILESYSTEM_NAME
            ) VALUES (
              #{roomNo}
            , #{roomImgName}
            , #{uploadPath}
            , ROOM_IMG_SEQ.NEXTVAL
            , #{filesystemName}
            )

mapper.update:
UPDATE ROOM
   SET ROOM_NAME = #{roomName}
     , INFO = #{info}
     , PRICE = #{price}
     , PEOPLE = #{people}
 WHERE ROOM_NO = #{roomNo}
